import React from 'react';

class GetData extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			data: '',
			monday: {
				young: 0
			},
			tuesday: {
				young: 0
			},
			wednesday: {
				young: 0
			},
			thursday: {
				young: 0
			},
			friday: {
				young: 0
			},
			saturday: {
				young: 0
			},
			sunday: {
				young: 0
			},
		};
	}

	getViewByWeekDay(data) {
		let date = new Date(data.n);
		let days = ['Sun', 'Mon', 'Tue', 'Wod', 'Thu', 'Fri', 'Sat'];
		let weekDay = days[date.getDay()];
		console.log(weekDay)
		let viewYoung = data.o[4].v;

		switch (weekDay) {
			case 'Sun':
				let sunday = {...this.state.sunday}
				sunday.young += viewYoung;
				this.setState({
					sunday: sunday
				});
				break;
			case 'Mon':
				let monday = {...this.state.monday}
				monday.young += viewYoung;
				this.setState({
					monday: monday
				});
				break;
			case 'Tue':
				let tuesday = {...this.state.tuesday}
				tuesday.young += viewYoung;
				this.setState({
					tuesday: tuesday
				});
				break;
			case 'Wod':
				let wednesday = {...this.state.wednesday}
				wednesday.young += viewYoung;
				this.setState({
					wednesday: wednesday
				});
				break;
			case 'Thu':
				let thursday = {...this.state.thursday}
				thursday.young += viewYoung;
				this.setState({
					thursday: thursday
				});
				break;
			case 'Fri':
				let friday = {...this.state.friday}
				friday.young += viewYoung;
				this.setState({
					friday: friday
				});
				break;
			case 'Sat':
				let saturday = {...this.state.saturday}
				saturday.young += viewYoung
				this.setState({
					saturday: saturday
				});
				break;
		}

	}

	async componentDidMount() {
		const url = 'https://analytics.3divi.ru/api/v2/statistics/user/2088/devices/dates/ages/?key=d3aa35bde5ef46899b91aca9c66e174e&b=2020/03/08%2012:00:00&e=2020/09/08%2012:00:00&tzo=0'
		let response = await fetch(url);
		let result = await response.json();
		this.setState({
			data: result.data
		})

		let divece7_date = result.data.o[7].o;

		// let view = result.data.o[7].o[0].o.find(o => o.n === "young").v;
		divece7_date.map(this.getViewByWeekDay)

		document.querySelector('.young').style.height = this.state.wednesday.young + 'px';
		document.querySelector('.adult').style.height = this.state.wednesday.young + 'px';
		// document.querySelector('.senior').style.height = view + 'px';

		// fetch(url)
		// .then((response) => {
		// 	response.json()
		// })
		// .then((result) => {
		// 	this.setState({
		// 		data: result
	 //    	})
		// 	console.log(result);
		// })

	}

	render() {
		return (
			<div>
				<div className="young"></div>
				<div className="adult"></div>
			</div>
		);
	}

}

export default GetData;
