import React from 'react';

class GetData extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			data: '',
			monday: {
				young: 0
			},
			tuesday: {
				young: 0
			},
			wednesday: {
				young: 0
			},
			thursday: {
				young: 0
			},
			friday: {
				young: 0
			},
			saturday: {
				young: 0
			},
			sunday: {
				young: 0
			},
		};
	}

	getWeekDay(date) {
	  let days = ['Sun', 'Mon', 'Tue', 'Wod', 'Thu', 'Fri', 'Sat'];
	  return days[date.getDay()];
	}

	async componentDidMount() {
		const url = 'https://analytics.3divi.ru/api/v2/statistics/user/2088/devices/dates/ages/?key=d3aa35bde5ef46899b91aca9c66e174e&b=2020/03/08%2012:00:00&e=2020/09/08%2012:00:00&tzo=0'
		let response = await fetch(url);
		let result = await response.json();
		this.setState({
			data: result.data
		})

		let divece7_date = result.data.o[7].o;

		// let view = result.data.o[7].o[0].o.find(o => o.n === "young").v;
		divece7_date.map((d) => {
			let date = new Date(d.n);
			let weekDay = this.getWeekDay(date);

			switch (weekDay) {
				case 'Sun':
					let sunday_view = this.state.sunday.young + d.o[4].v;
					// this.setState({
						// sunday.young: sunday_view
					// });
					console.log(sunday_view)
					break;
				case 'Mon':
					let monday_view = this.state.monday.young + d.o[4].v;
					// this.setState({
					// 	monday.young: monday_view
					// });
					console.log(monday_view)
					break;
				case 'Tue':
					let tuesday_view = this.state.tuesday.young + d.o[4].v;
					// this.setState({
					// 	tuesday.young: tuesday_view
					// });
					console.log(tuesday_view)
					break;
				case 'Wod':
					let wednesday_view = this.state.wednesday.young + d.o[4].v;
					// this.setState({
					// 	wednesday.young: wednesday_view
					// });
					console.log(wednesday_view)
					break;
				case 'Thu':
					let thursday_view = this.state.thursday.young + d.o[4].v;
					// this.setState({
					// 	thurday.young: thurday_view
					// });
					console.log(thursday_view)
					break;
				case 'Fri':
					let friday_view = this.state.friday.young + d.o[4].v;
					// this.setState({
					// 	friday.young: friday_view
					// });
					console.log(friday_view)
					break;
				case 'Sat':
					let saturday_view = this.state.saturday.young + d.o[4].v
					// this.setState({
					// 	saturday.young: saturday_view
					// });
					console.log(saturday_view)
					break;
			}

		})
		// let date = new Date(2014, 0, 3);
		// console.log( this.getWeekDay(date) );
		// console.log(result.data.o)
		// document.querySelector('.young').style.height = view + 'px';
		// document.querySelector('.adult').style.height = view + 'px';
		// document.querySelector('.senior').style.height = view + 'px';

		// fetch(url)
		// .then((response) => {
		// 	response.json()
		// })
		// .then((result) => {
		// 	this.setState({
		// 		data: result
	 //    	})
		// 	console.log(result);
		// })

	}

	render() {
		return (
			<div className="young"></div>
		);
	}

}

export default GetData;
